```java
import net.time4j.calendar.astro.SolarTime;

import java.io.*;

@SuppressWarnings("SameParameterValue")
public class SolarTimeTest {
    public static void main(String[] args) throws IOException, ClassNotFoundException {
        SolarTime solarTime = SolarTime.ofMecca();
        System.out.println(solarTime);
        byte[] bytes = serialize(solarTime);
        SolarTime solarTime2 = deserialize(bytes, SolarTime.class);
        System.out.println(solarTime2);
    }

    private static <T extends Serializable> byte[] serialize(T obj)
            throws IOException {
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        ObjectOutputStream oos = new ObjectOutputStream(baos);
        oos.writeObject(obj);
        oos.close();
        return baos.toByteArray();
    }

    private static <T extends Serializable> T deserialize(byte[] b, Class<T> clazz)
            throws IOException, ClassNotFoundException {
        ByteArrayInputStream bais = new ByteArrayInputStream(b);
        ObjectInputStream ois = new ObjectInputStream(bais);
        Object o = ois.readObject();
        return clazz.cast(o);
    }
}
```
