public class FVideoGrabber {

    private static int MAX_NO_FRAME_COUNT = 10;
    private FFmpegFrameGrabber grabber;
    private String rtsp;
    private volatile boolean isRunning = false;
    private volatile boolean isFirstTime = true;
    private int noFrameCount = 0;

    public FVideoGrabber( String rtsp) {
        this.rtsp = rtsp;
        this.grabber = initGrabber();
    }

    private FFmpegFrameGrabber initGrabber() {
        FFmpegFrameGrabber grabber = new FFmpegFrameGrabber(rtsp);
        grabber.setOption("rtsp_transport", "tcp");
        grabber.setOption("stimeout", "5000000");
        grabber.setFrameRate(20);
        return grabber;
    }

    public String getRtsp() {
        return rtsp;
    }

    public void setRtsp(String rtsp) {
        this.rtsp = rtsp;
    }

    public FFmpegFrameGrabber getGrabber() {
        return grabber;
    }

    public Frame grab() {
        if (!isRunning) {
            restart();
        }

        try {
            Frame grab = grabber.grab();
            for (int noFrameCount = 0; !isGrabbed(grab) && noFrameCount < 2; noFrameCount++) {
                grab = grabber.grab();
            }
            if (isGrabbed(grab)) {
                noFrameCount = 0;
            } else {
                noFrameCount++;
                if (noFrameCount > MAX_NO_FRAME_COUNT) {
                    isRunning = false;
                    noFrameCount = 0;
                    log.warn("The number of pull stream continuous errors exceeds[{}]times，Try to restart the streaming service【{}】", MAX_NO_FRAME_COUNT, rtsp);
                }
            }
            return grab;

        } catch (Exception e) {
            isRunning = false;
        }
        return null;

    }

    private boolean isGrabbed(Frame frame) {
        return !(frame == null || frame.image == null);
    }


    private void restart() {
        stop();
        start();
    }

    public void close() {
        if (grabber != null) {
            try {
                grabber.close();
                log.info("grabber closed,cameraInfo:" + rtsp);
            } catch (Exception e) {
                log.error("release the grabber error,CameraInfo:" + rtsp, e);
            }
        }
    }

    private void stop() {
        if (grabber != null && !isFirstTime) {
            try {
                grabber.stop();
                log.info("grabber stopped，CameraInfo" + rtsp);
            } catch (Exception e) {
                log.error("stop the recorder error,CameraInfo:" + rtsp, e);
            }
        }
    }

    private void start() {
        try {
            grabber.start();
            log.info("grabber started，cameraInfo" + rtsp);
        } catch (Exception e) {
            isRunning = false;
            log.error("start the recorder error,CameraInfo:" + rtsp, e);
            return;
        }
        isRunning = true;
        isFirstTime = false;
    }

    public boolean isRunning() {
        return isRunning;
    }
}
