public class FVideoGrabberThread implements Runnable {

    static {
        avutil.av_log_set_level(avutil.AV_LOG_ERROR);
    }

    private static int RECOGNITION_INTERVAL_MS = 1000;
    private String rtsp;
    private FVideoGrabber grabber;
    private volatile boolean isRunning = false;


    public FVideoGrabberExecutor(String rtsp) {
        this.rtsp = rtsp;
        this.grabber = new FVideoGrabber(rtsp);
    }

    public FVideoGrabber getGrabber() {
        return grabber;
    }


    @Override
    public void run() {
        isRunning = true;
        try {
            while (isRunning) {
                try {
                    Frame frame = this.grabber.grab();
                } catch (Exception e) {
                    log.error("Error in grabbing and pushing", e);
                }
            }
        }finally {
            doClose();
        }


    }


    private void doClose() {
        this.grabber.close();
    }

    public void close() {
        isRunning = false;
    }
}

