    cr.setPost(false);
    cr.setUrl(&quot;http://www.codenameone.com/files/theme/logo.png&quot;);
    NetworkManager.getInstance().addToQueue(cr);        
    hi.setLayout(new BoxLayout(BoxLayout.Y_AXIS));
    hi.show();        
}

public void stop() {
    current = Display.getInstance().getCurrent();
}

public void destroy() {
}
```

}

