import com.codename1.io.ConnectionRequest;
import com.codename1.io.NetworkManager;
import com.codename1.util.AsyncResource;
import java.io.IOException;
import java.io.InputStream;

/**
 *
 * @author shannah
 */
public class URLUtil {
    /**
     * Follows HTTP headers to find the *actual* URL for a URL.  This is handy
     * if you need to pass a URL to a service that doesn't support following 
     * redirects, such as the JavaFX media API.
     * @param url
     * @return 
     */
    public static AsyncResource<String> getRedirectURL(String url)  {
        AsyncResource<String> out = new AsyncResource<String>();
        //final String[] locationHdr = new String[1];
        ConnectionRequest req = new ConnectionRequest() {
            @Override
            protected void readHeaders(Object connection) throws IOException {
                super.readHeaders(connection);
                String l = getHeader(connection, "Location");
                if (l == null) {
                    out.complete(url);
                } else {
                    getRedirectURL(l).ready(res->{
                        out.complete(res);
                    }).except(ex->out.error(ex));
                }
                kill();
            }

            @Override
            protected void readResponse(InputStream input) throws IOException {
                input.close();
            }

        };
        req.setHttpMethod("HEAD");
        req.setUrl(url);
        req.setFollowRedirects(false);
        req.setFailSilently(true);
        req.setReadResponseForErrors(true);
        
        NetworkManager.getInstance().addToQueue(req);
        return out;
        
    }
}
